mapper.select:
SELECT BAF.*, BS.BS_NAME,BS.BS_NUMBER,BC.BC_NAME, BSC.BSC_TOTAL,BSC.BSC_RANK,BSC.BSC_ACADEMIC_RECORD,BSC.BSC_INTELLECTUAL_RANK FROM( SELECT * FROM BUS_AID_FINANCIALLY
AND ID = #{ID}
)BAF INNER JOIN BUS_STUDENT BS ON BS.ID = BAF.BS_ID INNER JOIN BUS_CLASS BC ON BC.ID = BS.BC_ID INNER JOIN BUS_STUDENT_COMPREHENSIVE BSC ON BSC.BS_ID = BAF.BS_ID AND BSC.BSC_YEAR = BAF.BAF_YEAR AND BSC.BSC_SEMESTER = BAF.BAF_SEMESTER

mapper.update:
UPDATE BUS_AID_FINANCIALLY
ID=#{ID}, SO_ID=#{SO_ID}, BS_ID=#{BS_ID}, BAF_YEAR=#{BAF_YEAR}, BAF_SEMESTER=#{BAF_SEMESTER}, BAF_TYPE=#{BAF_TYPE}, BAF_AID_TYPE=#{BAF_AID_TYPE}, BAF_REDUCTION_LEVEL=#{BAF_REDUCTION_LEVEL}, BAF_REDUCTION_QUOTA=#{BAF_REDUCTION_QUOTA}, BAF_DIFFICULTY_LEVEL=#{BAF_DIFFICULTY_LEVEL}, BAF_DIFFICULTY_TYPE=#{BAF_DIFFICULTY_TYPE}, BAF_ENTRY_TIME=#{BAF_ENTRY_TIME}, BUS_PROCESS=#{BUS_PROCESS}, BUS_PROCESS2=#{BUS_PROCESS2},
ID=#{ID}
