mapper.select:
SELECT BS.*,SDI.SDI_NAME AS BS_SEX_NAME,NDI.SDI_NAME AS BS_NATION_NAME,BDM.BDM_NAME,BC.BC_NAME
FROM(
    SELECT * FROM BUS_STUDENT


            AND ID = #{ID}


            AND BS_NUMBER = #{BS_NUMBER}


)BS
INNER JOIN BUS_DEPARTMENT BDM ON BDM.ID = BS.BDM_ID
INNER JOIN BUS_CLASS BC ON BC.ID = BS.BC_ID
LEFT JOIN SYS_DICT_INFO SDI ON SDI.SDT_CODE = 'BUS_SEX' AND SDI.SDI_CODE = BS.BS_SEX
LEFT JOIN SYS_DICT_INFO NDI ON NDI.SDT_CODE = 'BUS_NATION' AND NDI.SDI_CODE = BS.BS_NATION

SELECT BS.*
FROM(
    SELECT BS.*,SDI.SDI_NAME AS BS_SEX_NAME,NDI.SDI_NAME AS BS_NATION_NAME,BDM.BDM_NAME,BC.BC_NAME
    FROM(
        SELECT * FROM BUS_STUDENT


                AND ID = #{ID}


                AND BS_NUMBER = #{BS_NUMBER}


    )BS
    INNER JOIN BUS_DEPARTMENT BDM ON BDM.ID = BS.BDM_ID
    INNER JOIN BUS_CLASS BC ON BC.ID = BS.BC_ID
    LEFT JOIN SYS_DICT_INFO SDI ON SDI.SDT_CODE = 'BUS_SEX' AND SDI.SDI_CODE = BS.BS_SEX
    LEFT JOIN SYS_DICT_INFO NDI ON NDI.SDT_CODE = 'BUS_NATION' AND NDI.SDI_CODE = BS.BS_NATION
)BS


        ${whereClause}



    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

SELECT COUNT(*)
FROM(
    SELECT BS.*,SDI.SDI_NAME AS BS_SEX_NAME,NDI.SDI_NAME AS BS_NATION_NAME,BDM.BDM_NAME,BC.BC_NAME
    FROM(
        SELECT * FROM BUS_STUDENT


                AND ID = #{ID}


                AND BS_NUMBER = #{BS_NUMBER}


                AND BS_ID_CARD = #{BS_ID_CARD}


                AND BS_PHONE = #{BS_PHONE}


    )BS
    INNER JOIN BUS_DEPARTMENT BDM ON BDM.ID = BS.BDM_ID
    INNER JOIN BUS_CLASS BC ON BC.ID = BS.BC_ID
    LEFT JOIN SYS_DICT_INFO SDI ON SDI.SDT_CODE = 'BUS_SEX' AND SDI.SDI_CODE = BS.BS_SEX
    LEFT JOIN SYS_DICT_INFO NDI ON NDI.SDT_CODE = 'BUS_NATION' AND NDI.SDI_CODE = BS.BS_NATION
)BS


        ${whereClause}

mapper.update:
UPDATE BUS_STUDENT
ID=#{ID}, SO_ID=#{SO_ID}, BDM_ID=#{BDM_ID}, BC_ID=#{BC_ID}, BS_NAME=#{BS_NAME}, BS_NUMBER=#{BS_NUMBER}, BS_LENGTH=#{BS_LENGTH}, BS_ENROLMENT_YEAR=#{BS_ENROLMENT_YEAR}, BS_EXAMINEE_NUMBER=#{BS_EXAMINEE_NUMBER}, BS_ID_CARD=#{BS_ID_CARD}, BS_SEX=#{BS_SEX}, BS_NATION=#{BS_NATION}, BS_BANK=#{BS_BANK}, BS_BANK_CARD=#{BS_BANK_CARD}, BS_PHONE=#{BS_PHONE}, BS_PERMANENT_ADDRESS=#{BS_PERMANENT_ADDRESS}, BS_HOME_ADDRESS=#{BS_HOME_ADDRESS}, BS_ENTRY_TIME=#{BS_ENTRY_TIME},
ID=#{ID}
